#include "StdAfx.h"
#include "Utilities.h"

class Solution395 {
public:
    int longestSubstring(string s, int k) {
		if(s.empty()) return 0;
		if(k>s.length()) return 0;
		vector<int> counts(26, 0);
		int curNumLetters = 0;
		int curCompletedLetters = 0;
		vector<int> countsAtMax;
		int curNumLettersAtMax = 0;
		int curCompletedLettersAtMax = 0;
		int longest = 0;

		for(int i=0;i<s.length();i++){
			int j;
			if(longest > 0 && i>0){
				counts = countsAtMax;
				curNumLetters = curNumLettersAtMax;
				curCompletedLetters = curCompletedLettersAtMax;
				int p = s[i-1] - 'a';
				if( counts[p] == k){
					curCompletedLetters--;
				}
				counts[p]--;
				if(counts[p] == 0) curNumLetters--;		
				//j从已经算出的满足条件的最长长度开始计算
				j = i+longest-1;
			}else{
				counts.assign(26,0);
				curNumLetters = 0;
				curCompletedLetters = 0;
				j = i;
			}
			for(;j<s.length();j++){
				int pos = s[j] - 'a';
				if(counts[pos] == 0){					
					curNumLetters++;
				}
				counts[pos]++;
				if( counts[pos] == k){
					curCompletedLetters++;
				}
				if( j==i+longest-1){
					countsAtMax = counts;
					curNumLettersAtMax = curNumLetters;
					curCompletedLettersAtMax = curCompletedLetters;
				}
				if( curCompletedLetters == curNumLetters){
					if(j-i+1>=longest){
						longest = j-i+1;
						countsAtMax = counts;
						curNumLettersAtMax = curNumLetters;
						curCompletedLettersAtMax = curCompletedLetters;
					}
				}
			}
		}
		return longest;
    }

};

